mapper.select:
select 

from forward_browse_record
where machine_code = #{machineCode,jdbcType=VARCHAR}
  and create_time = #{createTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from forward_browse_record
where machine_code = #{machineCode,jdbcType=VARCHAR}
  and create_time = #{createTime,jdbcType=TIMESTAMP}

mapper.update:
update forward_browse_record
set user_id = #{userId,jdbcType=INTEGER},
  share_type = #{shareType,jdbcType=INTEGER},
  app_version = #{appVersion,jdbcType=VARCHAR},
  device_type = #{deviceType,jdbcType=VARCHAR},
  device_brand = #{deviceBrand,jdbcType=VARCHAR},
  device_version = #{deviceVersion,jdbcType=VARCHAR}
where machine_code = #{machineCode,jdbcType=VARCHAR}
  and create_time = #{createTime,jdbcType=TIMESTAMP}

