mapper.select:
      select count(*) FROM operate_article a 
	  where a.delete_flag=0 and a.publish_state=1

        and ${where}

      select a.article_id, a.category_id, a.title, a.summary, a.source, a.author, a.editor, a.publish_url, a.publish_time, 
      a.publish_state, a.tumb_pic_code, a.tumb_pic_code2, a.tumb_pic_code3, a.tumb, a.video_address, 
	  a.click_num, a.sort, a.is_special, a.is_send, a.is_picture, a.enclosure, a.create_time, a.update_time, 
	  a.delete_flag, CONCAT('https://picture.sxhalo.com/',tumb_pic_code) pic_path,c.category_name FROM operate_article a 
	  left join operate_article_category c on a.category_id=c.category_id
	  where a.delete_flag=0 and a.publish_state=1

        and ${where}


        order by ${order}


        limit ${start},${length}

select a.article_id, a.category_id, a.title, a.summary, a.source, a.author, a.editor, a.publish_url, a.publish_time, a.publish_state, a.tumb_pic_code, a.tumb_pic_code2, a.tumb_pic_code3, a.tumb, a.video_address, a.click_num, a.sort, a.is_special, a.is_send, a.is_picture, a.enclosure, a.create_time, a.update_time, a.delete_flag, CONCAT('https://picture.sxhalo.com/',tumb_pic_code) pic_path,c.category_name FROM operate_article a left join operate_article_category c on a.category_id=c.category_id where a.delete_flag=0 and a.publish_state=1
and ${where}
order by ${order}
