mapper.select:
select 

from sys_app
where app_name = #{appName,jdbcType=VARCHAR}
  and app_type = #{appType,jdbcType=VARCHAR}
  and version = #{version,jdbcType=VARCHAR}

mapper.delete:
delete from sys_app
where app_name = #{appName,jdbcType=VARCHAR}
  and app_type = #{appType,jdbcType=VARCHAR}
  and version = #{version,jdbcType=VARCHAR}

mapper.update:
update sys_app
set app_path = #{appPath,jdbcType=VARCHAR},
  issuance_time = #{issuanceTime,jdbcType=DATE},
  remark = #{remark,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  delete_flag = #{deleteFlag,jdbcType=INTEGER},
  verrify_state = #{verrifyState,jdbcType=INTEGER},
  accesskey = #{accesskey,jdbcType=VARCHAR}
where app_name = #{appName,jdbcType=VARCHAR}
  and app_type = #{appType,jdbcType=VARCHAR}
  and version = #{version,jdbcType=VARCHAR}

mapper.select:
select 

from sys_app

  where ${where}


  order by ${order}

select 

from sys_app

  where ${where}


  order by ${order}


  limit ${start},${length}
